mapper.select:

select 
	@rownu:=@rownum+1 as rn,
	t.board_no, t.board_title, t.board_content, t.board_writer, t.board_regdate, t.board_updatedate
from tbl_board t, (select @rownum:=0) rn
limit #{amount} offset (#{pageNum}-1) * #{amount}

mapper.update:
update tbl_board
set board_title = #{board_title}
, board_content = #{board_content}
, board_writer = #{board_writer}
, board_updatedate = now()
where board_no = #{board_no}
